mapper.select:
     select goods.*,type.name as typeName,user.name as username
     from goods left join type on type.id=goods.type_id
    left join user on user.id=goods.user_id
     
         
          and goods.name like concat('%', #{name}, '%')
         
         
          and goods.user_id = #{userId}
         
         
          and goods.type_id = #{typeId}
         
         
             and goods.shelve = #{shelve}
         
     
order by id desc

select goods.*,user.name as username,user.avatar as avatar from goods left join user on user.id=goods.user_id
and goods.name like concat('%', #{name}, '%')
and goods.type_id = #{typeId}
and goods.shelve = #{shelve}
order by id desc

select goods.*,user.name as username,user.avatar as avatar from goods left join user on user.id=goods.user_id
and goods.shelve = #{shelve}
order by view desc limit 4
